import React, { Component } from 'react';
import { Container, Row, Col } from 'reactstrap';
import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Button } from 'reactstrap';

class App extends Component {
  constructor (props){
    super(props);
    this.state = { isOn : true};
  } 
  buttonCick = () => {
    this.setState(prevState => ({
        isOn: !prevState.isOn
    }));
  }
  render() {
    return (
    <div>
      <div className="App">
        <Container>
          <Row>
              <Col xs = "12">
                <button className ="ToggleButton" onClick ={ this.buttonCick}>This is button
                  { this.state.isOn ? " ON":" OFF"}
                </button>               
              </Col>
              <Col xs="3" className = "Text-right"><h1> Time update </h1></Col>
              <Col xs="6" className = "Text-right"> <h2> Current time is: absj
                lorem nisndai nawujfnauj nfujienfuj ibenjuifnjsendaniunuinefuwen</h2></Col>
              <Col xs="3" className = "Text-right"><h1> Time update </h1></Col>
              <Col xs = "12">
                <Button color="primary">primary</Button>{' '}
                <Button color="secondary">secondary</Button>{' '}
                <Button color="success">success</Button>{' '}
                <Button color="info">info</Button>{' '}
                <Button color="warning">warning</Button>{' '}
                <Button color="danger">danger</Button>{' '}
                <Button color="link">link</Button>              
              </Col>
          </Row>

        </Container>  
      </div>
      <div className ="Button">

      </div>
    </div>

    );
  }
}

export default App;
